import React, { useRef } from 'react';
import './contact.css';
import {MdOutlineEmail} from 'react-icons/md';
import {RiMessengerLine} from 'react-icons/ri';
import {BsWhatsapp} from 'react-icons/bs';
import emailjs from  'emailjs-com'; 

const Contact = () => {
  const form = useRef();

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs.sendForm('service_zj2yy0e', 'template_jdpacjo', form.current, 'jm24Ayr4rJk4i1aW3')
    
    e.target.reset();
    //  .then((result) => {
    //      console.log(result.text);
    //   }, (error) => {
    //     console.log(error.text);
    //   });
  }
    return (
       <section id='contact'>
        <h5>Get In Touch</h5>
        <h2>Contact Me</h2>

        <div className='container contact__container'>
            <div className='contact__options'>
                <article className='contact__option'>
                    <MdOutlineEmail className='contact__option-icon'/>
                    <h4>
                        Email
                    </h4>
                    <h5>
                        plasdexcel14@gmail.com
                    </h5>
                    <a href='mailto:plasdexcel14@gmail.com' target='_blank'>Send a message</a>
                </article>
                <article className='contact__option'>
                    <RiMessengerLine className='contact__option-icon'/>
                    <h4>
                        Messenger
                    </h4>
                    <h5>
                        CanCodeAcademy
                    </h5>
                    <a href='https://m.me/placid_ughonu' target='_blank'>Send a message</a>
                </article>
                <article className='contact__option'>
                    <BsWhatsapp className='contact__option-icon'/>
                    <h4>
                        WhatsApp
                    </h4>
                    <h5>
                        +2348147418752
                    </h5>
                    <a href='http s://api.whatsapp.com/send?phone=+2348147418752' target='_blank'>Send a message</a>
                </article>
            </div>
            {/*END OF CONTACT OPTIONS */}
            <form ref={form} onSubmit={sendEmail}>
                <input type='text' name='name' placeholder='Your Full Name' required />
                <input type='email' name='email' placeholder='Your Email' required />
                <textarea name='message'rows='7' placeholder='Your Message' required ></textarea>
                <button type='submit' className='btn btn-primary'>Send Message</button>
            </form>
        </div>
       </section>
    )  
}
 
export default Contact;
